$('#datepicker').datepicker();
function today(){
	var d = new Date();
	var curr_date = d.getDate();
	var curr_month = d.getMonth() + 1;
	var curr_year = d.getFullYear();
	document.write(curr_date + "-" + curr_month + "-" + curr_year);
}

